import React, { Component } from 'react'
import './cssGlobal.css'
class Ajouter extends Component {
    constructor(props) {
        super(props);
        this.state = {
          items: []
        };
      }

      componentDidMount() {
        fetch("http://localhost:9400/list")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                items: result
              });
              console.log(result)
            },
          )
      }
    render() {
        return (
            <div className="container" id="Ajouter">
                <form action = "http://localhost:9400/list" method = "POST" className="row">
                <div className="col-md-4" id="Block">
                <label>Nom</label>&nbsp;&nbsp;<input type="text" name = "nom"/>
                </div>
                <div className="col-md-4">
                <label>Prenom</label>&nbsp;&nbsp;<input type="text" name = "prenom"/>
                </div>
                <div className="col-md-4" id="BlockBtn">
                <button className="btn btn-info" onClick={()=>{
                console.log(this.state.items.nom)
                }
                }>Ajouter</button>
                </div>



                </form>

            </div>
        )
    }
}
export default Ajouter
