import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
class List extends Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("http://localhost:8050/list")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result
            });         
            console.log(result)
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded} = this.state;
      if (error) {
        return <div>{console.log(error.message)}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <div>
              <button type="button" onClick={()=>{
                 let aff = document.getElementById('affichage')
                 //let img = document.getElementById('imageget')
                console.log(this.state.items)
                for(let i = 0; i<this.state.items.length; i++){   
                  aff.innerHTML +='<div ><div> <img id="reduirImage" src="'+this.state.items[i].image+'"/></div> <div> Nom: '+ this.state.items[i].nom +'<br/> Prenom: '+ this.state.items[i].prenom +'</div></div><br>'
                  //img.innerHTML = this.state.items[1].image
                  console.log(this.state.items[0].image)
                 } 
              }}>Lister</button>

             <p id="affichage"></p> 
          </div>
        );
      }
    }
  }
  export default List
