import React, {Component} from 'react'
class List extends Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("http://localhost:8050/list")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });
            let aff = document.getElementById('affichage')
            console.log(result.length)
            for(let i = 0; i<result.length; i++){   
             aff.innerHTML = result[i].nom
                console.log(result[i].nom)
            }


          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, result } = this.state;
      if (error) {
        return <div>{console.log(error.message)}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <div>
             <p id="affichage"></p> 
          </div>
        );
      }
    }
  }
  export default List
