import React, {Component} from 'react'
//import { Route} from 'react-router-dom';
class List extends Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("http://localhost:8050/list")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });           

          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded} = this.state;
      if (error) {
        return <div>{console.log(error.message)}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <div>
              <button type="button" onClick={()=>{
                let aff = document.getElementById('affichage')
                for(let i = 0; i<this.state.result.length; i++){   
                 aff.innerHTML += this.state.result[i].nom +' ' + this.state.result[i].prenom +'<br>'
                    console.log(this.state.result[i].nom)
                }
              }}>Lister</button>
             <p id="affichage"></p> 
          </div>
        );
      }
    }
  }
  export default List
