import React, { Component } from 'react';
import Request from 'react-http-request';

const proxy = require('http-proxy-middleware');


class App extends Component {
  prox = function(app){
    app.use(proxy('/api', { target: 'http://localhost:5000/' }));
  }; 
  render() {
    return (
      console.log(prox)
    );
  }
}
export default App
